import React, { useState} from 'react'
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"
import axios from "axios"




const App = () => {
  const [text,setText] = useState("search movie")
  const [movie,setMovie] = useState([])
  const changeText = (event)=>{
    setText(event.target.value)
  }
  const getMovie =(e)=>{
    e.preventDefault();
    axios.get(`http://www.omdbapi.com/?s=${text}&apikey=c1b697a9`)
    .then((response)=>{
      console.log(response);
      setMovie(response.data.Search)
    })
  }
  return (
    <>

      <h1>LIVE</h1>
      <nav className="navbar navbar-expand-lg navbar navbar-dark bg-dark">
        <a className="navbar-brand" href="target">Movie App</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
              <a className="nav-link" href="target">Home</a>
            </li>

          </ul>
          <form className="col-md-20 bg-light text-right"onSubmit = {getMovie}>
            <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" value={text} onChange={changeText}/>
            <button className="btn btn-success btn-lg float-right" type="submit">Search</button>
          </form>
        </div>
      </nav>


      <div className="container">
        <div className="row">
          {
            movie.map((value, index) => {
              return (
                <div className="col-3">
                  <div className="card" style={{ width: "18rem" }}>
                    <img src={value.Poster}className="card-img-top" alt="..." />
                    <div className="card-body">
                      <h3 className="card-title">{value.Year}</h3>
                      <h4 className="card-text">{value.Title}</h4>
                    </div>
                  </div>
                </div>
              )
            })
          }
        </div>
      </div>

    </>
  )
}

export default App







