import React, { userState} from 'react'
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"




const App = () => {
  const [text,setText] = userState("search movie")

  const changeText = (event)=>{
        setText(event.target.value)
      }
  const getMovie = async () => {
    try {
      const response = await fetch(`http://www.omdbapi.com/?s=${text}&apikey=c1b697a9`);
      const data = await response.json()
      console.log(data);
    } catch (err) {
      console.log(err)
    }
  }
  // useEffect(() => {
  //   getMovie()
  // }, []);
  return (
    <>

      <h1>LIVE</h1>
      <nav className="navbar navbar-expand-lg navbar navbar-dark bg-dark">
        <a className="navbar-brand" href="target">Movie App</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
              <a className="nav-link" href="target">Home</a>
            </li>

          </ul>
          <form className="form-inline my-2 my-lg-0"onSubmit = {getMovie}>
            <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" value={text} onChange={changeText}/>
            <button className="btn btn-success btn-lg float-right" type="submit">Search</button>
          </form>
        </div>
      </nav>

    </>
  )
}

export default App







