import React, { useEffect } from 'react'
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"
const App = () => {
  const getCovidData=async()=>{
    try{
   const response = await fetch("http://www.omdbapi.com/?s=kabhi&apikey=c1b697a9");
   const data = await response.json()
   console.log(data);
    }catch(err){
    console.log(err)
     }
  }
  useEffect(()=>{
    getCovidData()
  },[]);
  return (
    <>

    <h1>LIVE</h1>
    <a href="target" onClick={ev => {ev.preventDefault();}}>"Hello A Tag"</a>
    </>
  )
}

export default App







