import React, { useState} from 'react'
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"
import axios from "axios"




const App = () => {
  const [text,setText] = useState("search movie")
  const [movie,setMovie] = useState([])
  const changeText = (event)=>{
    setText(event.target.value)
  }
  const getMovie =(e)=>{
    e.preventDefault();
    axios.get(`http://www.omdbapi.com/?s=${text}&apikey=c1b697a9`)
    .then((response)=>{
      console.log(response);
      setMovie(response.data.Search)
    })
  }

  function Menu(props) {
  const list = [{
    item: 'Home',
    itemLink: '/'
 },]
 const menuItems = list.map((list, index) => {
    return <NavLink className={`item_${index + 1}`} to={`${list.itemLink}`} key={index}> <span>{list.item}</span></NavLink >
 })
 return (
 <nav className="navmenu">{menuItems}</nav>
 )
}








  return (
    <>
      <nav className="navbar navbar-expand-lg navbar navbar-dark bg-dark">
  <div className="container-fluid">
    <a className="navbar-brand" href="target">Movie APP</a>
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <a className="nav-link active" aria-current="page" href="target">Home</a>
        </li>
        <li className="nav-item">
          <a className="nav-link active" aria-current="page" href="/SignUp">SignUp</a>
        </li>
      </ul>
      <form className="d-flex" role="search"  onSubmit = {getMovie}>
        <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" value={text} onChange={changeText}/>
        <button className="btn btn-outline-success" type="submit">Search</button>
      </form>
    </div>
  </div>
</nav>


      <div className="container">
        <div className="row">
          {
            movie.map((value, index) => {
              return (
                <div className="col-3">
                  <div className="card" style={{ width: "18rem" }}>
                    <img src={value.Poster}className="card-img-top" alt="..." />
                    <div className="card-body">
                      <h3 className="card-title">{value.Year}</h3>
                      <h4 className="card-text">{value.Title}</h4>
                    </div>
                  </div>
                </div>
              )
            })
          }
        </div>
      </div>

    </>
  )
}

export default App







